import React from 'react';

export class Camera extends React.Component {
  state = {
    cameraLink: "http://143.107.235.2:8000/sensors/media"
  }
  cameraRefresh = () => {
    this.setState({
      cameraLink: "http://143.107.235.2:8000/sensors/media"
    })
    console.log('a');
    setInterval(this.cameraRefresh, 30*1000);
  }
  componentDidMount(){
    this.cameraRefresh();
  }
  render(){
    return(
      <div className="camera">
        <img
        src={this.state.cameraLink}
        position="relative"
        width="100%"
        alt="camera feed"
        />
      </div>
    )
  }
}
