import React from 'react';
import axios from 'axios';

export class Camera extends React.Component {
  state = {
    image: null
  }
  cameraRefresh = () => {
    console.log('a');
    this.forceUpdate();
    setInterval(this.cameraRefresh, 30*1000);
  }
  componentDidMount(){
    this.cameraRefresh();
    axios.get('http://143.107.235.2:8000/sensors/media')
      .then(response => {
        console.log(response);
        this.setState(
          {
            image: response.data
          }
        );
      })
      .catch(error => {
        console.log('erro com o request da imagem da camera');
        console.log(error);
      });
  }
  render(){
    return(<div className="camera">
      <img
      src={this.state.image}
      width="100%"
      height="100%"
      alt="camera feed"
      />
    </div>
    )
  }
}
