import React from 'react';
import axios from 'axios';

export class WeatherForecast extends React.Component {
  state = {
    //constantes de informação geral
    cidade: null,
    frase: null,
    data: null,
    //constantes do primeiro dia, hoje
    iconManha: null,
    iconTarde: null,
    iconNoite: null,
    probabilidadeChuva: null,
    precipitacaoChuva: null,
    temperaturaMin: null,
    temperaturaMax: null,
    umidadeMin: null,
    umidadeMax: null,
    ventoMin: null,
    ventoMax: null,
  }
  todayWeatherForecast = () =>{
    //utilizando o método get para pegar os dados da API do climatempo
    axios.get('http://apiadvisor.climatempo.com.br/api/v1/forecast/locale/3680/days/15?token=2e30b5793b0c662d9b901520663bd4d0')
      .then(response => {
        console.log(response.data);
        this.setState(
          {
            //constantes de informação geral
            cidade: response.data.name,
            frase: response.data.data[0].text_icon.text.pt,
            //constantes do primeiro dia, hoje
            data: response.data.data[0].date_br,
            iconManha: "../../images/" + response.data.data[0].text_icon.icon.morning + ".png",
            iconTarde: "../../images/" + response.data.data[0].text_icon.icon.afternoon + ".png",
            iconNoite: "../../images/" + response.data.data[0].text_icon.icon.night + ".png",
            probabilidadeChuva: response.data.data[0].rain.probability,
            precipitacaoChuva: response.data.data[0].rain.precipitation,
            temperaturaMin: response.data.data[0].temperature.min,
            temperaturaMax: response.data.data[0].temperature.max,
            umidadeMin: response.data.data[0].humidity.min,
            umidadeMax: response.data.data[0].humidity.max,
            ventoMin: response.data.data[0].wind.velocity_min,
            ventoMax: response.data.data[0].wind.velocity_max,
          }
        );
      })
      .catch(error => {
        console.log('erro com o request da previsão do tempo do climatempo');
        console.log(error);
      })
    setTimeout(this.todayWeatherForecast, 1000*60*60);
    this.forceUpdate();

  }
  componentDidMount() {
    this.todayWeatherForecast();
    }
  render() {
    return (<div>
    <div className="weather-one">
      <h3>{this.state.cidade}, {this.state.data}</h3>
    </div>
    <div className="weather-two">
      <table align="center">
        <tbody>
          <tr>
            <td><img src={this.state.iconManha} width="50%" alt="morning weather forecast"/></td>
            <td><img src={this.state.iconTarde} width="50%" alt="afternoon weather forecast"/></td>
            <td><img src={this.state.iconNoite} width="50%" alt="night weather forecast"/></td>
          </tr>
          <tr>
            <td>Manhã</td>
            <td>Tarde</td>
            <td>Noite</td>
          </tr>
        </tbody>
      </table>
      <h3>{this.state.frase}</h3>
    </div>
    <div className="weather-three">
      <table align="center">
        <thead>
          <tr>
            <th><img src="../../images/rain.png" width="50%" alt="rain icon"/></th>
            <th><img src="../../images/humidity.png" width="50%" alt="humidity icon"/></th>
            <th><img src="../../images/thermometer.png" width="50%" alt="thermometer icon"/></th>
            <th><img src="../../images/wind.png" width="50%" alt="wind icon"/></th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td rowSpan="2">{this.state.probabilidadeChuva}% {this.state.precipitacaoChuva}mm</td>
            <td>{this.state.umidadeMax}%</td>
            <td>{this.state.temperaturaMax}°C</td>
            <td>{this.state.ventoMax} km/h</td>
          </tr>
          <tr>
            <td>{this.state.umidadeMin}%</td>
            <td>{this.state.temperaturaMin}°C</td>
            <td>{this.state.ventoMin} km/h</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
  );
  }
}
